script:
$wrapper = @"
param([Parameter(ValueFromRemainingArguments=$true)][string[]]$args)
$originalPwd = Get-Location
$resolvedArgs = @()
$nextIsOutput = $false
foreach ($arg in $args) {
  if ($nextIsOutput) {
    if ([System.IO.Path]::IsPathRooted($arg)) { $resolvedArgs += $arg } else { $resolvedArgs += (Join-Path $originalPwd $arg) }
    $nextIsOutput = $false
  } elseif ($arg -eq '-o') {
    $resolvedArgs += '-o'
    $nextIsOutput = $true
  } elseif (Test-Path $arg) {
    $resolvedArgs += (Resolve-Path $arg).Path
  } else {
    $resolvedArgs += $arg
  }
}
Set-Location "$dir\libexec"
& (Get-Command bb).Source 'fill_docx.bb' @resolvedArgs
"@
